import 'package:ninja_asn1/ninja_asn1.dart';
import 'package:ninja_asn1/src/util/bigint.dart';
import 'package:ninja_hex/ninja_hex.dart';
import 'package:test/test.dart';

void main() {
  group('ASN1', () {
    final integer = BigInt.parse(
        '');

    final octet = bigIntToBytes(integer);

    test('ASN1Sequence.encode', () {
      expect(
          hexEncode(ASN1Sequence(
              [ASN1Integer(integer), ASN1Null(), ASN1Boolean(true)]).encode()),
          '');

      expect(
          ASN1Sequence.decode(hexDecode(
                  ''))
              .children,
          [ASN1Integer(integer), ASN1Null(), ASN1Boolean(true)]);
    });

    test('ASN1Boolean.encode', () {
      expect(hexEncode(ASN1Boolean(false).encode()), '010100');
      expect(ASN1Boolean.decode(hexDecode('010100')).value, false);

      expect(hexEncode(ASN1Boolean(true).encode()), '0101ff');
      expect(ASN1Boolean.decode(hexDecode('0101ff')).value, true);
    });

    test('ASN1Integer.encode', () {
      expect(hexEncode(ASN1Integer(integer).encode()),
          '');

      expect(
          ASN1Integer.decode(hexDecode(
                  ''))
              .value,
          integer);
    });

    test('ASN1OctetString.encode', () {
      expect(hexEncode(ASN1OctetString(octet).encode()),
          '');

      expect(
          ASN1OctetString.decode(hexDecode(
                  ''))
              .value,
          octet);
    });

    test('ASN1Null.encode', () {
      expect(hexEncode(ASN1Null().encode()), '0500');
      ASN1Null.decode(hexDecode('0500'));
    });

    test('ASN1ObjectIdentifier.encode', () {
      expect(
          hexEncode(
              ASN1ObjectIdentifier.fromString('1.25.140.134569').encode()),
          '060641810c889b29');

      expect(
          ASN1ObjectIdentifier.decode(hexDecode('060641810c889b29'))
              .objectIdentifierAsString,
          '1.25.140.134569');
    });

    test('ASN1BitString.encode', () {
      expect(
          bytesToBigInt(ASN1BitString(octet, unusedBits: 4).encode())
              .toRadixString(16),
          '');

      {
        final bitString = ASN1BitString.decode(hexDecode(
            ''));
        expect(bitString.unusedBits, 4);
        expect(bitString.bitString, octet);
      }
    });
  });
}
